using SkiaSharp;
using SQLite;
using System;
using System.Drawing;
using System.IO;
using Xamarin.Forms;

namespace ComicBookCatalog.Models
{
    [Table("ComicBook")]
    public class ComicBook
    {
        public ComicBook()
        {
            Temporary = false;
        }

        public ComicBook(bool temporary)
        {
            Temporary = temporary;
            if (temporary)
            {
                UUID = Guid.NewGuid().ToString();
            }
        }

        /// <summary>
        /// Unikalny identyfikator
        /// </summary>    
        [PrimaryKey]
        public string UUID { get; set; }

        /// <summary>
        /// Tytuł
        /// </summary>
        public string Title { get; set; }

        /// <summary>
        /// Seria
        /// </summary>
        public string Series { get; set; }

        /// <summary>
        /// Marka
        /// </summary>
        public string Brand { get; set; }

        /// <summary>
        /// Tom
        /// </summary>
        public int Volume { get; set; }

        /// <summary>
        /// Okładka
        /// </summary>
        public string Cover { get; set; }

        /// <summary>
        /// Czy ulubiony?
        /// </summary>
        public bool Favorite { get; set; }

        /// <summary>
        /// Adres internetowy
        /// </summary>
        public string Url { get; set; }

        /// <summary>
        /// Notatka
        /// </summary>
        public string Note { get; set; }

        /// <summary>
        /// ISBN
        /// </summary>
        public string ISBN { get; set; }

        /// <summary>
        /// Wydawca
        /// </summary>
        public string Publisher { get; set; }

        /// <summary>
        /// Scenarzyści
        /// </summary>
        public string Writers { get; set; }

        /// <summary>
        /// Na półce?
        /// </summary>
        public bool OnBookShelf { get; set; }

        /// <summary>
        /// 
        /// </summary>
        public DateTime Updated { get; set; }

        /// <summary>
        /// 
        /// </summary>
        public string Language { get; set; }

        /// <summary>
        /// 
        /// </summary>
        [Ignore]
        public string Index
        {
            get
            {
                string f = Title.Substring(0, 1);
                if (int.TryParse(f, out int n))
                {
                    return "#";
                }

                return f;
            }
        }

        /// <summary>
        /// Czy numer tomu jest widoczny?
        /// </summary>
        [Ignore]
        public bool IsVolumeVisible
        {
            get
            {
                return Volume > 0;
            }
        }

        /// <summary>
        /// Obrazek okładki
        /// </summary>
        [Ignore]
        public ImageSource CoverImage
        {
            get
            {
                // nie mamy okładki, wiec ją wygenerujemy
                if (string.IsNullOrEmpty(Cover))
                {
                    var data = Publisher switch
                    {
                        "DC" => "",
                        "MARVEL" => "",
                        "Image Comics" => "",
                        "Non Stop Comics" => "",
                        _ => Base.Config.CoverImagePlaceholder,
                    };

                    return ImageSource.FromStream(
                        () => new MemoryStream(Convert.FromBase64String(data))
                    );
                }
                // mamy okładkę w pliku
                else if (Cover.StartsWith("cover_"))
                {
                    var path = Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData);
                    var filepath = Path.Combine(path, Cover);

                    return ImageSource.FromFile(filepath);
                }
                // mamy w base64
                else
                {
                    return ImageSource.FromStream(
                        () => new MemoryStream(Convert.FromBase64String(Cover))
                    );
                }                
            }
        }

        /// <summary>
        /// Pełny tytuł
        /// </summary>
        [Ignore]
        public string FullTitle
        {
            get
            {
                if (IsVolumeVisible)
                {
                    return string.Format("{0} vol. {1}", Title, Volume);
                }

                return Title;
            }
        }

        /// <summary>
        /// Seria i marka
        /// </summary>
        [Ignore]
        public string SeriesAndBrand
        {
            get
            {
                if (string.IsNullOrEmpty(Series) == false && string.IsNullOrEmpty(Brand) == false)
                {
                    return string.Format("{0}/{1}", Series, Brand);
                }

                if (string.IsNullOrEmpty(Series) == false && string.IsNullOrEmpty(Brand))
                {
                    return Series;
                }

                if (string.IsNullOrEmpty(Series) && string.IsNullOrEmpty(Brand) == false)
                {
                    return Brand;
                }

                return string.Empty;
            }
        }

        /// <summary>
        /// 
        /// </summary>
        [Ignore]
        public string Sequence
        {
            get
            {
                if (IsVolumeVisible)
                {
                    return Volume.ToString();
                }

                return Title;
            }
        }        

        /// <summary>
        /// 
        /// </summary>
        [Ignore]
        public bool Temporary { get; private set; }

        /// <summary>
        /// 
        /// </summary>
        [Ignore]
        public bool CanDisplayCoverImage 
        { 
            get
            {
                return string.IsNullOrEmpty(Cover) == false;
            } 
        }
    }
}